    public function fetch_ohlcv(string $symbol, $timeframe = '1h', ?int $since = null, ?int $limit = null, $params = array ()): array {
        /**
         * fetches historical candlestick data containing the open, high, low, and close price, and the volume of a $market
         * @see https://api.arzplus.net/
         * @param {string} $symbol unified $symbol of the $market to fetch OHLCV data for
         * @param {string} $timeframe the length of time each candle represents
         * @param {int} [$since] timestamp in ms of the earliest candle to fetch
         * @param {int} [$limit] the maximum amount of candles to fetch
         * @param {array} [$params] extra parameters specific to the exchange API endpoint
         * @return {int[][]} A list of candles ordered, open, high, low, close, volume
         */
        $this->load_markets();
        $market = $this->market($symbol);
        $endTime = Date.now ();
        $request = array(
            'symbol' => $market['id'],
            'from' => ($endTime / 1000) - (24 * 60 * 60),
            'to' => $endTime / 1000,
            'resolution' => $this->safe_string($this->timeframes, $timeframe, $timeframe),
            'countBack' => 300,
        );
        if ($since !== null) {
            $request['from'] = $since / 1000;
        }
        if ($limit !== null) {
            $request['countBack'] = $limit;
        }
        if ($timeframe !== null) {
            $request['resolution'] = $this->safe_string($this->timeframes, $timeframe, $timeframe);
        }
        $request['from'] = $this->safe_integer($request, 'from');
        $request['to'] = $this->safe_integer($request, 'to');
        $response = $this->publicGetApiV1MarketTradingviewOhlcv ($request);
        $ohlcvs = array();
        for ($i = 0; $i < count($response); $i++) {
            $ohlcvs[] = [
                $this->safe_value($response[$i], 'time'),
                $this->safe_value($response[$i], 'open'),
                $this->safe_value($response[$i], 'high'),
                $this->safe_value($response[$i], 'low'),
                $this->safe_value($response[$i], 'close'),
                $this->safe_value($response[$i], 'volume'),
            ];
        }
        return $this->parse_ohlcvs($ohlcvs, $market, $timeframe, $since, $limit);
    }

    public function fetch_order_book(string $symbol, ?int $limit = null, $params = array ()): array {
        /**
         * fetches information on open orders with bid (buy) and ask (sell) prices, volumes and other data for multiple markets
         * @see https://api.arzplus.net/
         * @param {string[]|null} symbols list of unified $market symbols, all symbols fetched if null, default is null
         * @param {int} [$limit] max number of entries per orderbook to return, default is null
         * @param {array} [$params] extra parameters specific to the exchange API endpoint
         * @return {array} a dictionary of ~@link https://docs.ccxt.com/#/?id=order-book-structure order book structures~ indexed by $market $symbol
         */
        $this->load_markets();
        $market = $this->market($symbol);
        $request = array(
            'symbol' => $market['id'],
        );
        $orderBook = $this->publicGetApiV1MarketDepth ($request);
        $timestamp = Date.now ();
        return $this->parse_order_book($orderBook, $symbol, $timestamp, 'bids', 'asks', 'price', 'amount');
    }

    public function sign($path, $api = 'public', $method = 'GET', $params = array (), $headers = null, $body = null) {
        $query = $this->omit($params, $this->extract_params($path));
        $url = $this->urls['api']['public'] . '/' . $path;
        if ($params['stats'] !== null) {
            $url = $url . '?' . $this->urlencode($query);
        }
        if ($path === 'api/v1/market/tradingview/ohlcv') {
            $url = $url . '?' . $this->urlencode($query);
        } elseif ($params['symbol'] !== null) {
            $url = $url . '/' . $params['symbol'];
        }
        $headers = array( 'Content-Type' => 'application/json' );
        return array( 'url' => $url, 'method' => $method, 'body' => $body, 'headers' => $headers );
    }
