    public function fetch_tickers(?array $symbols = null, $params = array ()): PromiseInterface {
        return Async\async(function () use ($symbols, $params) {
            /**
             * fetches price tickers for multiple markets, statistical information calculated over the past 24 hours for each market
             * @see https://www.efex.pro/fa/api-documentation
             * @param {string[]|null} $symbols unified $symbols of the markets to fetch the $ticker for, all market tickers are returned if not assigned
             * @param {array} [$params] extra parameters specific to the exchange API endpoint
             * @return {array} a dictionary of ~@link https://docs.ccxt.com/#/?id=$ticker-structure $ticker structures~
             */
            Async\await($this->load_markets());
            if ($symbols !== null) {
                $symbols = $this->market_symbols($symbols);
            }
            $response = Async\await($this->publicGetGetAllRate ());
            $result = array();
            for ($i = 0; $i < count($response); $i++) {
                $ticker = Async\await($this->parse_ticker($response[$i]));
                $symbol = $ticker['symbol'];
                $result[$symbol] = $ticker;
            }
            return $this->filter_by_array_tickers($result, 'symbol', $symbols);
        }) ();
    }

    public function fetch_ticker(string $symbol, $params = array ()): PromiseInterface {
        return Async\async(function () use ($symbol, $params) {
            /**
             * fetches a price $ticker, a statistical calculation with the information calculated over the past 24 hours for a specific market
             * @see https://www.efex.pro/fa/api-documentation
             * @param {string} $symbol unified $symbol of the market to fetch the $ticker for
             * @param {array} [$params] extra parameters specific to the exchange API endpoint
             * @return {array} a ~@link https://docs.ccxt.com/#/?id=$ticker-structure $ticker structure~
             */
            $ticker = Async\await($this->fetch_tickers(array( $symbol )));
            return $ticker[$symbol];
        }) ();
    }

    public function parse_ticker($ticker, ?array $market = null): array {
        // array(
        // $market => "BTC-USDT",
        // buy_price => 67462.89,
        // sell_price => 68670.21
        // ),
        $marketType = 'otc';
        $marketId = $this->safe_value($ticker, 'market');
        $marketinfo = $this->market($marketId);
        $symbol = $this->safe_symbol($marketId, $market, null, $marketType);
        $bid = $this->safe_float($ticker, 'buy_price');
        $ask = $this->safe_float($ticker, 'sell_price');
        $last = $this->safe_float($ticker, 'buy_price');
        if ($marketinfo['quote'] === 'IRT') {
            $bid /= 10;
            $ask /= 10;
            $last /= 10;
        }
        return $this->safe_ticker(array(
            'symbol' => $symbol,
            'timestamp' => null,
            'datetime' => null,
            'high' => null,
            'low' => null,
            'bid' => $bid,
